mapper.select:
SELECT quantity
FROM inventory
WHERE impitemname = #{expitemname} AND itemname = #{itemname}

mapper.update:

UPDATE expsub SET
	status = #{status}
WHERE expitemname = #{expitemname} AND itemname = #{itemname}

UPDATE inventory SET
	quantity = quantity - #{expquantity}
WHERE impitemname = #{expitemname} AND itemname = #{itemname}
