mapper.insert:
MERGE INTO inventory
USING impsub
ON inventory.impnum = impsub.inventorynum 
WHEN MATCHED THEN
UPDATE SET
	inventorynum = inventorynum.nextval
	impitemname = #{impitemname}
	itemname = #{itemname}
	impdepot = #{impdepot}
	implocation = #{implocation}
	quantity = #{quantity}
	impdate = #{impdate}
WHEN NOT MATCHED THEN
	INSERT (inventorynum, impitemname, itemname, impdepot, implocation, quantity, impdate)
	VALUES (inventorynum.nextval, #{impitemname}, #{itemname}, #{impdepot}, #{implocation}, #{quantity}, #{impdate})

mapper.update:
UPDATE imp SET
	supply1 = #{supply1},
    impdate = #{impdate},
    note = #{note}
WHERE impnum = #{impnum}
